// ===========================
// Import React
// ===========================
import React from "react";

// ===========================
// Import chef images
// ===========================
import Chef1 from "../assets/chef2.jpg"; // Maria's image
import Chef2 from "../assets/chef1.jpg"; // Marcus's image
import Chef3 from "../assets/chef3.jpg"; // Luis
import Chef4 from "../assets/chef4.jpg"; // Ana

// ===========================
// ChefGrid component
// ===========================
export default function ChefGrid() {
  return (
    <section className="chef-grid container">
      {/* Grid heading */}
      <h2>Meet Our Chefs</h2>

      {/* Chef cards container */}
      <div className="chef-cards">
        {/* Maria */}
        <div className="chef-card">
          <img src={Chef1} alt="Maria" />
          <h3>Maria</h3>
          <p>Expert in Italian cuisine and fresh flavors.</p>
        </div>

        {/* Marcus */}
        <div className="chef-card">
          <img src={Chef2} alt="Marcus" />
          <h3>Marcus</h3>
          <p>Grilling master and farm-to-table specialist.</p>
        </div>

        {/* Luis */}
        <div className="chef-card">
          <img src={Chef3} alt="Luis" />
          <h3>Luis</h3>
          <p>Pastry chef creating unforgettable desserts.</p>
        </div>

        {/* Ana */}
        <div className="chef-card">
          <img src={Chef4} alt="Ana" />
          <h3>Ana</h3>
          <p>Seafood expert bringing ocean flavors to life.</p>
        </div>
      </div>
    </section>
  );
}
