import React from "react";
import { Link } from "react-router-dom";

/**
 * Nav component
 * This is the main navigation bar for the Chefs2Table website.
 * It includes the logo/brand, menu links, and a temporary dashboard login button.
 */
export default function Nav() {
  return (
    // The <nav> element is the semantic container for navigation links
    <nav className="nav">

      {/* =========================
          Brand / Logo Section
          ========================= */}
      <div className="brand">
        {/* Link back to homepage */}
        <Link to="/">
          {/* Logo container */}
          {/* Replace the empty div with an image for proper display */}
          <div className="logo">
            <img 
              src="/assets/images/logo.png"  // Path to your logo image
              alt="Chefs2Table Logo"         // Accessibility text
            />
          </div>
        </Link>

        {/* Site title and tagline */}
        <div>
          <h1>Chefs2Table</h1>
          <small>Fresh chefs, fresh food</small>
        </div>
      </div>

      {/* =========================
          Navigation Links Section
          ========================= */}
      <div className="links">
        {/* Main menu links */}
        <Link to="/">Home</Link>
        <Link to="/chefs">Chefs</Link>
        <Link to="/about">About</Link>
        <Link to="/contact">Contact</Link>

        {/* Temporary Dashboard Login Button */}
        {/* Will later be replaced by a proper authentication flow */}
        <Link to="/dashboard" className="btn gold">
          Login
        </Link>
      </div>
    </nav>
  );
}
