// ===========================
// Import React
// ===========================
import React from "react";

// ===========================
// Import chef images from current folder
// ===========================
import Chef1Img from "./chef1.jpg";
import Chef2Img from "./chef2.jpg";
import Chef3Img from "./chef3.jpg";
import Chef4Img from "./chef4.jpg";
import Chef5Img from "./chef5.jpg";

// ===========================
// ChefGrid component
// ===========================
export default function ChefGrid() {
  return (
    <section className="chef-grid container">
      {/* Grid heading */}
      <h2>Meet Our Chefs</h2>

      {/* Chef cards */}
      <div className="grid" style={{ display: "flex", gap: "24px", flexWrap: "wrap", justifyContent: "center" }}>
        {[Chef1Img, Chef2Img, Chef3Img, Chef4Img, Chef5Img].map((img, index) => (
          <div key={index} className="chef-card" style={{ textAlign: "center" }}>
            <img src={img} alt={`Chef ${index + 1}`} style={{ width: "200px", borderRadius: "12px" }} />
            <h3>{`Chef ${index + 1}`}</h3>
            <p>Delicious meals, exceptional deals</p>
          </div>
        ))}
      </div>
    </section>
  );
}
