// ===========================
// Import React
// ===========================
import React from "react";

// ===========================
// Import chef images (exact filenames)
// ===========================
import MariaImg from "../assets/Maria.png";
import MarcusImg from "../assets/Marcus.png";
import LuisImg from "../assets/Luis.png";
import AnaImg from "../assets/Ana.png";

// ===========================
// ChefGrid component
// ===========================
export default function ChefGrid() {
  return (
    // Section wrapper for chef grid, using container class for max-width and padding
    <section className="chef-grid container">
      {/* ===========================
          Grid heading
      =========================== */}
      <h2>Meet Our Chefs</h2>

      {/* ===========================
          Grid of chef cards
      =========================== */}
      <div className="grid">
        {/* ===========================
            Chef Card: Maria
        =========================== */}
        <div className="chef-card">
          {/* Chef photo */}
          <img src={MariaImg} alt="Maria" />
          {/* Chef name */}
          <h3>Maria</h3>
          {/* Chef tagline / description */}
          <p>Delicious meals, exceptional deals</p>
        </div>

        {/* ===========================
            Chef Card: Marcus
        =========================== */}
        <div className="chef-card">
          <img src={MarcusImg} alt="Marcus" />
          <h3>Marcus</h3>
          <p>Delicious meals, exceptional deals</p>
        </div>

        {/* ===========================
            Chef Card: Luis
        =========================== */}
        <div className="chef-card">
          <img src={LuisImg} alt="Luis" />
          <h3>Luis</h3>
          <p>Delicious meals, exceptional deals</p>
        </div>

        {/* ===========================
            Chef Card: Ana
        =========================== */}
        <div className="chef-card">
          <img src={AnaImg} alt="Ana" />
          <h3>Ana</h3>
          <p>Delicious meals, exceptional deals</p>
        </div>
      </div>
    </section>
  );
}
