import React from "react";
import { Link } from "react-router-dom";

/**
 * Navigation bar component
 * -------------------------------------------------------
 * Contains the site brand/logo on the left and navigation
 * links on the right. The “Login” button temporarily routes
 * to the dashboard page. 
 */
export default function Nav() {
  return (
    <header className="site-header">
      <nav className="nav container">
        {/* Brand section with logo + title */}
        <div className="brand">
          <Link to="/">
            {/* Logo block (styled in CSS) */}
            <div className="logo" />
          </Link>

          {/* Brand text */}
          <div>
            <h1>Chefs2Table</h1>
            <small>Delicious meals, exceptional deals</small>
          </div>
        </div>

        {/* Navigation links */}
        <div className="links">
          <Link to="/">Home</Link>
          <Link to="/chefs">Chefs</Link>
          <Link to="/about">About</Link>
          <Link to="/contact">Contact</Link>

          {/* Temporary dashboard login button */}
          <Link to="/dashboard" className="btn gold">
            Login
          </Link>
        </div>
      </nav>
    </header>
  );
}
