// ===========================
// Import React
// ===========================
import React from "react";

// ===========================
// Import CSS for styling
// ===========================
import './ChefGrid.css';

// ===========================
// Import chef images
// ===========================
import MariaImg from "../assets/chef2.jpg"; // Maria
import MarcusImg from "../assets/chef1.jpg"; // Marcus
import LuisImg from "../assets/chef3.jpg"; // Luis
import AnaImg from "../assets/chef4.jpg";  // Ana

// ===========================
// ChefGrid component
// ===========================
export default function ChefGrid() {
  return (
    <section className="chef-grid container">
      {/* Grid heading */}
      <h2>Meet Our Chefs</h2>

      {/* Chef cards */}
      <div className="grid">
        {/* Marcus */}
        <div className="chef-card">
          <img src={MarcusImg} alt="Marcus" />
          <h3>Chef Marcus</h3>
          <p>Delicious meals, exceptional deals</p>
        </div>

        {/* Maria */}
        <div className="chef-card">
          <img src={MariaImg} alt="Maria" />
          <h3>Chef Maria</h3>
          <p>Delicious meals, exceptional deals</p>
        </div>

        {/* Luis */}
        <div className="chef-card">
          <img src={LuisImg} alt="Luis" />
          <h3>Chef Luis</h3>
          <p>Delicious meals, exceptional deals</p>
        </div>

        {/* Ana */}
        <div className="chef-card">
          <img src={AnaImg} alt="Ana" />
          <h3>Chef Ana</h3>
          <p>Delicious meals, exceptional deals</p>
        </div>
      </div>
    </section>
  );
}
