// ===========================
// Navigation Component
// ===========================

import React from "react";
import { Link } from "react-router-dom";

// Importing logo image from assets folder
import logoImg from "../assets/logo.png";  // <-- ensures the logo displays correctly

export default function Nav() {
  return (
    <nav className="nav">
      {/* ===========================
          Brand / Logo
          =========================== */}
      <div className="brand">
        <Link to="/">
          {/* Logo container: uses imported image */}
          <div className="logo">
            <img
              src={logoImg}           // Path to the logo image
              alt="Chefs2Table Logo"  // Accessibility alt text
              style={{
                width: "100%",        // Fill the logo container
                height: "100%",
                objectFit: "cover",
                borderRadius: "14px"  // match CSS border radius
              }}
            />
          </div>
        </Link>

        {/* Site title and tagline */}
        <div>
          <h1>Chefs2Table</h1>
          <small>Delicious meals, exceptional deals</small>
        </div>
      </div>

      {/* ===========================
          Navigation Menu Links
          =========================== */}
      <div className="links">
        <Link to="/">Home</Link>
        <Link to="/chefs">Chefs</Link>
        <Link to="/about">About</Link>
        <Link to="/contact">Contact</Link>

        {/* Temporary dashboard login button */}
        <Link to="/dashboard" className="btn gold">
          Login
        </Link>
      </div>
    </nav>
  );
}
