import React from "react";

// ===========================
// Import Chef Images
// ===========================
// Swapped Marcus and Maria images to match gender
import chef1 from "../assets/images/chef2.jpg"; // Marcus
import chef2 from "../assets/images/chef1.jpg"; // Maria
import chef3 from "../assets/images/chef3.jpg";
import chef4 from "../assets/images/chef4.jpg";
import chef5 from "../assets/images/chef5.jpg";

// ===========================
// Chef Data Array
// ===========================
const chefs = [
  { name: "Maria", image: chef2 },
  { name: "Marcus", image: chef1 },
  { name: "Chef3", image: chef3 },
  { name: "Chef4", image: chef4 },
  { name: "Chef5", image: chef5 },
];

// ===========================
// ChefGrid Component
// ===========================
export default function ChefGrid() {
  return (
    <section className="chef-grid container">
      {/* Section heading */}
      <h2>Meet Our Chefs</h2>

      {/* Grid of chef cards */}
      <div className="grid">
        {chefs.map((chef, index) => (
          <div className="chef-card" key={index}>
            {/* Chef image */}
            <img src={chef.image} alt={chef.name} />

            {/* Chef name */}
            <h3>{chef.name}</h3>
          </div>
        ))}
      </div>
    </section>
  );
}
