// src/components/ChefGrid.js
import React from "react";

// Import images safely
// Make sure the filenames exactly match these imports
import MariaImg from "../assets/Maria.png";    // Female chef
import MarcusImg from "../assets/Marcus.png";  // Male chef
import LuisImg from "../assets/Luis.png";
import AnaImg from "../assets/Ana.png";

export default function ChefGrid() {
  // Array of chef objects for mapping over
  const chefs = [
    {
      name: "Maria",
      description: "Expert in Italian cuisine with a passion for fresh ingredients.",
      image: MariaImg,
    },
    {
      name: "Marcus",
      description: "Specializes in French pastries and delicate desserts.",
      image: MarcusImg,
    },
    {
      name: "Luis",
      description: "Grill master and barbecue specialist.",
      image: LuisImg,
    },
    {
      name: "Ana",
      description: "Vegan chef focused on healthy, flavorful meals.",
      image: AnaImg,
    },
  ];

  return (
    <section className="chef-grid container">
      {/* Grid heading */}
      <h2>Meet Our Chefs</h2>

      {/* Grid of chefs */}
      <div className="grid">
        {chefs.map((chef, index) => (
          <div key={index} className="chef-card">
            <img
              src={chef.image}
              alt={chef.name}
              className="chef-photo"
            />
            <h3>{chef.name}</h3>
            <p>{chef.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
