// ===========================
// Import React
// ===========================
import React from "react";

// ===========================
// Import chef images
// Make sure these filenames match your ./src/assets/ folder exactly
// ===========================
import Chef1 from "../assets/chef1.jpg";
import Chef2 from "../assets/chef2.jpg";
import Chef3 from "../assets/chef3.jpg";
import Chef4 from "../assets/chef4.jpg";

// ===========================
// ChefGrid component
// ===========================
export default function ChefGrid() {
  return (
    <section className="chef-grid container">
      {/* Grid heading */}
      <h2>Meet Our Chefs</h2>

      {/* Chef cards container */}
      <div className="chef-cards">
        {/* Chef 1 */}
        <div className="chef-card">
          <img src={Chef1} alt="Chef 1" />
          <h3>Maria</h3>
          <p>Expert in Italian cuisine and fresh flavors.</p>
        </div>

        {/* Chef 2 */}
        <div className="chef-card">
          <img src={Chef2} alt="Chef 2" />
          <h3>Marcus</h3>
          <p>Grilling master and farm-to-table specialist.</p>
        </div>

        {/* Chef 3 */}
        <div className="chef-card">
          <img src={Chef3} alt="Chef 3" />
          <h3>Luis</h3>
          <p>Pastry chef creating unforgettable desserts.</p>
        </div>

        {/* Chef 4 */}
        <div className="chef-card">
          <img src={Chef4} alt="Chef 4" />
          <h3>Ana</h3>
          <p>Seafood expert bringing ocean flavors to life.</p>
        </div>
      </div>
    </section>
  );
}
