// ===========================
// ChefGrid.js
// Displays the grid of chefs on the homepage
// ===========================
import React from "react";

// Import chef images
import mariaImg from "../assets/maria.png";
import marcusImg from "../assets/marcus.png";
import luisImg from "../assets/luis.png";
import anaImg from "../assets/ana.png";

export default function ChefGrid() {
  // ===========================
  // Chef Data Array
  // Each chef has id, name, description, and image
  // ===========================
  const chefs = [
    {
      id: 1,
      name: "Maria",
      description: "Expert in Italian cuisine and homemade pastas.",
      image: mariaImg,
    },
    {
      id: 2,
      name: "Marcus",
      description: "Specializes in gourmet BBQ and smoked meats.",
      image: marcusImg,
    },
    {
      id: 3,
      name: "Luis",
      description: "Master of sushi and traditional Japanese dishes.",
      image: luisImg,
    },
    {
      id: 4,
      name: "Ana",
      description: "Pastry chef creating exquisite desserts daily.",
      image: anaImg,
    },
  ];

  // ===========================
  // JSX Return
  // ===========================
  return (
    <section className="chef-grid container">
      {/* Grid heading */}
      <h2>Meet Our Chefs</h2>

      {/* Grid of chef cards */}
      <div className="grid">
        {chefs.map((chef) => (
          <div className="chef-card" key={chef.id}>
            {/* Chef image */}
            <img src={chef.image} alt={chef.name} />

            {/* Chef name */}
            <h3>{chef.name}</h3>

            {/* Chef description */}
            <p>{chef.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
